function scrollToHowToGuide(){const e=document.getElementById("welcome");let t=document.getElementById("how-to-play").offsetTop+e.offsetHeight-70;window.scrollTo({top:t,behavior:"smooth"})}function setPixleCountdown(){let e=new Date,t=new Date(e);t.setUTCDate(t.getUTCDate()+1),t.setHours(0,0,0,0);let o=t.getTime()-e.getTime(),n=Math.floor(o%864e5/36e5),s=Math.floor(o%36e5/6e4),i=Math.floor(o%6e4/1e3);const l=document.getElementById("pixle-countdown");l.querySelector("div.pixle-countdown-progress").style.width=100*(e.getHours()/24+e.getMinutes()/60/24+e.getSeconds()/60/60/24)+"%",l.querySelector("span.hours").innerHTML=n<10?"0"+n:n.toString(),l.querySelector("span.minutes").innerHTML=s<10?"0"+s:s.toString(),l.querySelector("span.seconds").innerHTML=i<10?"0"+i:i.toString()}document.onreadystatechange=(()=>{if("complete"===document.readyState){const e=document.getElementById("main-navbar"),t=document.getElementById("welcome");window.onscroll=(()=>{let o=t.offsetTop+t.offsetHeight;if(window.scrollY>o){if(!e.classList.contains("do-not-show"))return;e.classList.remove("do-not-show")}else{if(e.classList.contains("do-not-show"))return;e.classList.add("do-not-show")}}),this.setPixleCountdown(),window.setInterval(()=>{this.setPixleCountdown()},1e3)}});