function scrollToHowToGuide(){let o=document.getElementById("how-to-play").offsetTop;window.scrollTo({top:o-70,behavior:"smooth"})}document.onreadystatechange=(()=>{if("complete"===document.readyState){const o=document.getElementById("main-navbar"),t=document.getElementById("welcome");window.onscroll=(()=>{let e=t.offsetTop+t.offsetHeight;if(window.scrollY>e){if(!o.classList.contains("do-not-show"))return;o.classList.remove("do-not-show")}else{if(o.classList.contains("do-not-show"))return;o.classList.add("do-not-show")}})}});