document.onreadystatechange=(()=>{if("complete"===document.readyState){const o=document.getElementById("main-navbar"),t=document.getElementById("welcome");document.body.onscroll=(()=>{let e=t.offsetTop+t.offsetHeight;if(document.body.scrollTop>e){if(!o.classList.contains("do-not-show"))return;o.classList.remove("do-not-show")}else{if(o.classList.contains("do-not-show"))return;o.classList.add("do-not-show")}})}});