function scrollToHowToGuide(){const o=document.getElementById("welcome");let e=document.getElementById("how-to-play").offsetTop+o.offsetHeight-70;window.scrollTo({top:e,behavior:"smooth"})}document.onreadystatechange=(()=>{if("complete"===document.readyState){const o=document.getElementById("main-navbar"),e=document.getElementById("welcome");window.onscroll=(()=>{let t=e.offsetTop+e.offsetHeight;if(window.scrollY>t){if(!o.classList.contains("do-not-show"))return;o.classList.remove("do-not-show")}else{if(o.classList.contains("do-not-show"))return;o.classList.add("do-not-show")}})}});